configuration.encoder:
// Convert a value to a byte array in little-endian format
function toLittleEndianByteArray(value, byteSize) {
    var byteArray = [];
    for (var i = 0; i < byteSize; i++) {
        byteArray.push(value & 0xFF);
        value = value >> 8;
    }
    return byteArray;
}

// Fixed timestamp (UNIX time 1722772800 corresponds to "2024-08-04T12:00:00Z")
var timestamp = 1722772800;
var timestampBytes = toLittleEndianByteArray(timestamp, 4); // 4 bytes

// Fixed message ID (12345 corresponds to [0x39, 0x30] in little-endian format)
var messageId = 12345;
var messageIdBytes = toLittleEndianByteArray(messageId, 2); // 2 bytes

// Task type is always "valveControl"
var taskTypeByte = 0x01; // 1 byte for "valveControl"

// Instruction type and valve number are hardcoded; valveState depends on msg.Valve1State
var instructionTypeByte = 0x09;  // 1 byte for "09"
var valveNumberByte = 0x01;  // 1 byte for "01"
var valveStateByte = msg.Valve1State === true ? 0x01 : 0x00;  // 1 byte for "01" (open) or "00" (closed)

// Combine all bytes into a single array
var finalBytes = [].concat(
    timestampBytes,  // Timestamp
    messageIdBytes,  // Message ID
    [taskTypeByte],  // Task Type
    [instructionTypeByte, valveNumberByte, valveStateByte]  // Task Data
);

// Convert the byte array to a Base64 string
var base64Payload = btoa(String.fromCharCode.apply(null, finalBytes));

// Construct the downlink message
var data = {
    downlinks: [{
        f_port: 2,  // Specify the port
        confirmed: true,
        frm_payload: base64Payload,  // The Base64 encoded payload
        priority: "NORMAL"
    }]
};

// Prepare the result object to return
var result = {
    contentType: "JSON",  // Specify the content type of the downlink message
    data: JSON.stringify(data),  // The downlink data in JSON format
    metadata: {
        devId: 'eui-70b3d57ed006996d'  // Example static devId
    }
};

// Return the result object
return result;

