// Explicitly set the valveState based on Valve1State
if (msg.Valve1State === true) {
    valveState = "01";  // Valve state open
} else {
    valveState = "00";  // Valve state closed
}

// Construct the detailed downlink message
var detailedMessage = {
    metadata: {
        timestamp: new Date().toISOString(),  // Current timestamp
        messageId: Math.floor(Math.random() * 100000)  // Random messageId
    },
    task: {
        type: "valveControl",  // Task type is valve control
        data: {
            instructionType: instructionType,  // Fixed instructionType "09"
            valveNumber: "01",  // Hardcoded valve number
            valveState: valveState  // Use the explicitly set valveState
        }
    }
};

// Convert the detailed message to a JSON string
var jsonString = JSON.stringify(detailedMessage);

// Encode the JSON string to Base64
var base64Payload = btoa(jsonString);

// Construct the data object with the required structure
var data = {
    downlinks: [{
        f_port: 2,  // Specify the port
        confirmed: false,  // Add the confirmed field
        frm_payload: base64Payload,  // The Base64 encoded payload
        priority: "NORMAL"  // Priority level
    }]
