configuration.encoder:
// Encode downlink data from incoming Rule Engine message

// msg - JSON message payload downlink message json
// msgType - type of message, for ex. 'ATTRIBUTES_UPDATED', 'POST_TELEMETRY_REQUEST', etc.
// metadata - list of key-value pairs with additional data about the message
// integrationMetadata - list of key-value pairs with additional data defined in Integration executing this converter

/** Encoder **/

  // Construct the downlink message
    var data = {
        downlinks: [{
            f_port: 2,
            confirmed: true,
            frm_payload: btoa(msg.Valve1State),  // Encode the Valve1State as Base64
            priority: "NORMAL"
        }]
    };

    // Prepare the result object to return
    var result = {

        // Specify the content type of the downlink message
        contentType: "JSON",

        // The downlink data in JSON format
        data: JSON.stringify(data),

        // Optional metadata object in key/value format
        metadata: {
            devId: 'eui-70b3d57ed006996d',  // devId

        }

    };

    // Return the result object
    return result;

configuration.tbelEncoder:
// Define the instructionType as always "09" for valve control
var instructionType = "09";

// Define variable to hold the valveState value
var valveState;

// Explicitly set the valveState based on Valve1State
if (msg.Valve1State === true) {
    valveState = "01";  // Valve state open
} else {
    valveState = "00";  // Valve state closed
}

// Construct the detailed downlink message
var detailedMessage = {
    metadata: {
        timestamp: new Date().toISOString(),  // Current timestamp
        messageId: Math.floor(Math.random() * 100000)  // Random messageId
    },
    task: {
        type: "valveControl",  // Task type is valve control
        data: {
            instructionType: instructionType,  // Fixed instructionType "09"
            valveNumber: "01",  // Hardcoded valve number
            valveState: valveState  // Use the explicitly set valveState
        }
    }
};

// Convert the detailed message to a JSON string
var jsonString = JSON.stringify(detailedMessage);

// Encode the JSON string to Base64
var base64Payload = btoa(jsonString);

// Construct the data object with the required structure
var data = {
    downlinks: [{
        f_port: 2,  // Specify the port
        confirmed: false,  // Add the confirmed field
        frm_payload: base64Payload,  // The Base64 encoded payload
        priority: "NORMAL"  // Priority level
    }]
};

// Construct the result object
var result = {
    contentType: "JSON",  // Specify the content type
    //data: data,
    data: JSON.stringify(data),  // Convert the data object to a JSON string
    metadata: {
        devId: 'eui-70b3d57ed006996d'  // Example static devId
    }
};

// Return the result object
return result;
